import 'package:json_annotation/json_annotation.dart';

part 'todo.g.dart';

/// Todo model for the backend.
///
/// Uses snake_case for JSON serialization to match REST API conventions.
@JsonSerializable(fieldRename: FieldRename.snake)
class Todo {
  Todo({
    required this.id,
    required this.title,
    this.description,
    this.completed = false,
    this.priority = 3,
    this.dueDate,
    required this.updatedAt,
    this.deletedAt,
    this.createdAt,
  });

  /// Unique identifier (UUID), generated by the client.
  final String id;

  /// Todo title (required).
  final String title;

  /// Optional description.
  final String? description;

  /// Whether the todo is completed.
  final bool completed;

  /// Priority level (1-5, where 1 is highest).
  final int priority;

  /// Optional due date.
  final DateTime? dueDate;

  /// Last update timestamp (set by server).
  final DateTime updatedAt;

  /// Soft delete timestamp (null if not deleted).
  final DateTime? deletedAt;

  /// Creation timestamp.
  final DateTime? createdAt;

  factory Todo.fromJson(Map<String, dynamic> json) => _$TodoFromJson(json);

  Map<String, dynamic> toJson() => _$TodoToJson(this);

  /// Creates a copy with updated fields.
  Todo copyWith({
    String? id,
    String? title,
    String? description,
    bool? completed,
    int? priority,
    DateTime? dueDate,
    DateTime? updatedAt,
    DateTime? deletedAt,
    DateTime? createdAt,
  }) {
    return Todo(
      id: id ?? this.id,
      title: title ?? this.title,
      description: description ?? this.description,
      completed: completed ?? this.completed,
      priority: priority ?? this.priority,
      dueDate: dueDate ?? this.dueDate,
      updatedAt: updatedAt ?? this.updatedAt,
      deletedAt: deletedAt ?? this.deletedAt,
      createdAt: createdAt ?? this.createdAt,
    );
  }
}
